operation.add:
// E-Leads feed route
if (isset($this->request->get['_route_'])) {
	$route = trim($this->request->get['_route_'], '/');
	if (preg_match('#^eleads-yml/([a-zA-Z_-]+)\.xml$#', $route, $m)) {
		$this->request->get['route'] = 'extension/module/eleads';
		$this->request->get['lang'] = $m[1];
		return;
	}
}
